function updateElementIndex(el, prefix, ndx) {
    var id_regex = new RegExp('(' + prefix + '-\\d+)');
    var replacement = prefix + '-' + ndx;
    if ($(el).attr("for")) $(el).attr("for", $(el).attr("for").replace(id_regex, replacement));
    if (el.id) el.id = el.id.replace(id_regex, replacement);
    if (el.name) el.name = el.name.replace(id_regex, replacement);
}

function cloneMore(selector, prefix) {
    var newElement = $(selector).clone(true);
    var total = $('#id_' + prefix + '-TOTAL_FORMS').val();
        newElement.find('select,input').each(function() {
            var attrname = $(this).attr('name');
            var attrid = $(this).attr('id');
            if (typeof attrname !== typeof undefined && attrid !== false && typeof attrid !== typeof undefined && attrid !== false) {
                var name = $(this).attr('name').replace('-' + (total-1) + '-', '-' + total + '-');
                var id = 'id_' + name;
                var opciones =  $(this).attr('id')+'_chosen'
                $(this).attr({'name': name, 'id': id,'placeholder' : 'cantidad'}).val('').removeAttr('checked');
                $(this).children('div:last').append('<button class="btn btn-success add-form-row rounded">Añadir más</button>')
            }
            else if(typeof attrid !== typeof undefined && attrid !== false){
                var name = $(this).attr('id').replace('id_formset_' + (total-1) + '_fk_id_producto_chosen', 'id_formset__' + total + '_fk_id_producto_chosen');
            }
        });
        

   
        

    
    total++;
    $('#id_' + prefix + '-TOTAL_FORMS').val(total);
    $(selector).after(newElement);
    var conditionRow = $('.form-group:not(:last)');
    conditionRow.find('.btn.add-form-row')
    .removeClass('btn-success').addClass('btn-danger')
    .removeClass('add-form-row').addClass('remove-form-row')
    .html('<span class="fas fa-minus-circle" aria-hidden="true"></span>');
    return false;
}
function deleteForm(prefix, btn) {
    var total = parseInt($('#id_' + prefix + '-TOTAL_FORMS').val());
    if (total > 1){
        btn.closest('.form-group').remove();
        var forms = $('.form-group');
        $('#id_' + prefix + '-TOTAL_FORMS').val(forms.length);
        for (var i=0, formCount=forms.length; i<formCount; i++) {
            $(forms.get(i)).find(':input').each(function() {
                updateElementIndex(this, prefix, i);
            });
        }
    }
    return false;
}